pragma solidity ^0.8.17;

import "./UsingCryptoPunksMosaicRegistryStructs.sol";

interface ICryptoPunksMosaicRegistry is UsingCryptoPunksMosaicRegistryStructs {
    // TODO: fill it out
    function create(
        uint256 punkId,
        uint64 totalClaimableCount,
        uint256 purchasePrice,
        uint256 minReservePrice,
        uint256 maxReservePrice
    ) external returns (uint192 originalId);

    function mint(
        address contributor,
        uint192 originalId
    ) external returns (uint256 mosaicId);

    function bid(
        uint192 originalId,
        uint256 price
    ) external payable returns (uint256 newBidId);

    function grantMintAuthority(address addr) external;
}
","keccak256":"0x47fe147d7633cfc178a4c87774456d238eb99814f80d9a59563a71f4606b36ef","license":"MIT"},"src/UsingCryptoPunksGroupRegistryStructs.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

interface UsingCryptoPunksGroupRegistryStructs {
    enum GroupStatus {
        Invalid,
        Open,
        Claimable
    }

    enum GroupLifeCycle {
        // the group does not exist
        Nonexistent,
        // the group is open and funding is ongoing
        Active,
        // the group has not procured the target original within the expiry
        Lost,
        // the group has successfully purchased and acquired the target original
        Won
    }

    struct Group {
        uint192 id;
        address creator;
        uint256 targetPunkId;
        uint256 targetMaxPrice;
        // @dev Must be a divisor (10, 100, etc.)
        uint64 totalTicketSupply;
        uint256 unitTicketPrice;
        uint256 totalContribution;
        uint64 ticketsBought;
        uint40 expiresAt; // inclusive, in seconds, as in block.timestamp
        GroupStatus status;
        uint256 purchasePrice; // price at which the target is bought
        uint192 originalId;
        string metadataUri;
    }

    event GroupCreated(
        uint192 indexed groupId,
        address indexed creator,
        uint256 targetMaxPrice,
        uint64 totalTicketSupply,
        uint256 unitTicketPrice
    );

    event GroupWon(uint192 indexed groupId);

    event Contributed(
        address indexed contributor,
        uint192 indexed groupId,
        uint96 indexed ticketQuantity
    );

    event Claimed(
        address indexed claimer,
        uint192 indexed groupId,
        uint256 indexed mosaicId
    );
}
