src/CryptoPunksGroupRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {Strings} from "lib/openzeppelin-contracts/contracts/utils/Strings.sol";
import {ReentrancyGuardUpgradeable} from "lib/openzeppelin-contracts-upgradeable/contracts/security/ReentrancyGuardUpgradeable.sol";
import {ERC1155Upgradeable} from "lib/openzeppelin-contracts-upgradeable/contracts/token/ERC1155/ERC1155Upgradeable.sol";
import {UUPSUpgradeable} from "lib/openzeppelin-contracts-upgradeable/contracts/proxy/utils/UUPSUpgradeable.sol";
import {AccessControlUpgradeable} from "lib/openzeppelin-contracts-upgradeable/contracts/access/AccessControlUpgradeable.sol";

import "./lib/BasisPoint.sol";
import "./external/ICryptoPunksMarket.sol";
import "./ICryptoPunksGroupRegistry.sol";
import "./ICryptoPunksMosaicRegistry.sol";
import "./CryptoPunksMuseum.sol";
import "./CryptoPunksGroupStorage.sol";

// TODO: Migrate custom revert error messages to byte constants
contract CryptoPunksGroupRegistry is
    ICryptoPunksGroupRegistry,
    ERC1155Upgradeable,
    UUPSUpgradeable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable
{
    /**
     * Arithmetic constants
     */
    uint64 public constant MIN_RESERVE_PRICE_BPS = 7000; // 70%
    uint64 public constant MAX_RESERVE_PRICE_BPS = 50000; // 500%

    /**
     * Business logic constants
     */
    uint64 public constant TICKET_SUPPLY_PER_GROUP = 100;
    /**
     * @dev can create and curate the active group
     */
    bytes32 public constant CURATOR_ROLE = keccak256("CURATOR_ROLE");

    CryptoPunksMuseum public museum;

    function initialize(address museumAddress) public initializer {
        __ERC1155_init("CryptoPunksGroup Ticket");
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(CURATOR_ROLE, msg.sender);
        museum = CryptoPunksMuseum(museumAddress);
    }

    modifier onlyWhenActive() {
        require(
            address(museum) != address(0) && museum.isActive(),
            "Museum must be active"
        );
        _;
    }

    modifier onlyValidGroup(uint192 groupId) {
        require(
            groupId <= CryptoPunksGroupStorage.get().latestGroupId,
            "Invalid groupId"
        );
        _;
    }

    function create(
        uint256 targetPunkId,
        uint256 targetMaxPrice
    ) external onlyRole(CURATOR_ROLE) onlyWhenActive returns (uint192 groupId) {
        require(
            getGroupLifeCycle(CryptoPunksGroupStorage.get().latestGroupId) !=
                GroupLifeCycle.Active,
            "Ongoing group exists"
        );

        ++CryptoPunksGroupStorage.get().latestGroupId;
        uint64 totalTicketSupply = TICKET_SUPPLY_PER_GROUP;
        uint256 unitTicketPrice = targetMaxPrice / totalTicketSupply;
        uint192 groupId = CryptoPunksGroupStorage.get().latestGroupId;

        Group storage newGroup = CryptoPunksGroupStorage.get().groups[groupId];
        newGroup.id = CryptoPunksGroupStorage.get().latestGroupId;
        newGroup.creator = msg.sender;
        newGroup.targetPunkId = targetPunkId;
        newGroup.targetMaxPrice = targetMaxPrice;
        newGroup.totalTicketSupply = totalTicketSupply;
        newGroup.unitTicketPrice = unitTicketPrice;
        newGroup.status = GroupStatus.Open;
        // TODO: Make it configurable
        newGroup.expiresAt = uint40(block.timestamp + 604800);

        emit GroupCreated(
            groupId,
            msg.sender,
            targetMaxPrice,
            totalTicketSupply,
            unitTicketPrice
        );
        return groupId;
    }

    function contribute(
        uint192 groupId,
        uint64 ticketQuantity
    ) external payable onlyValidGroup(groupId) onlyWhenActive {
        Group storage group = CryptoPunksGroupStorage.get().groups[groupId];

        uint256 ticketsLeft = group.totalTicketSupply - group.ticketsBought;
        require(
            ticketQuantity <= ticketsLeft,
            "Fewer tickets remaining than requested"
        );

        uint256 ethReceived = msg.value;
        uint256 ethRequired = group.unitTicketPrice * ticketQuantity;
        require(ethReceived == ethRequired, "Contribution must be exact");

        address contributor = msg.sender;
        group.totalContribution += ethReceived;
        group.ticketsBought += ticketQuantity;
        CryptoPunksGroupStorage.get().refundableTickets[groupId][
            contributor
        ] += ticketQuantity;

        _mint(contributor, groupId, ticketQuantity, "");

        emit Contributed(contributor, groupId, ticketQuantity);
    }

    /**
     * @dev Can be tried as long as the group is OPEN
     */
    function buy(
        uint192 groupId
    )
        external
        nonReentrant
        onlyRole(CURATOR_ROLE)
        onlyValidGroup(groupId)
        onlyWhenActive
    {
        // Internal prerequisites
        require(
            address(museum.mosaicRegistry()) != address(0x0),
            "Exhibit registry must be set"
        );

        // Stakeholder and group status prerequisites
        Group storage group = CryptoPunksGroupStorage.get().groups[groupId];
        uint256 punkId = group.targetPunkId;
        (, , , uint256 offeredPrice, ) = museum
            .cryptoPunksMarket()
            .punksOfferedForSale(punkId);
        require(group.ticketsBought == TICKET_SUPPLY_PER_GROUP, "Not sold out");
        require(
            group.totalContribution >= offeredPrice,
            "Offered price is greater than the current contribution"
        );

        // Purchase
        museum.cryptoPunksMarket().buyPunk{value: offeredPrice}(punkId);
        require(
            museum.cryptoPunksMarket().punkIndexToAddress(punkId) ==
                address(this),
            "Unexpected ownership"
        );

        // TODO: Double-check the possibility of reentrancy attacks when the same punk ID is used again later
        museum.cryptoPunksMarket().transferPunk(
            address(museum.mosaicRegistry()),
            group.targetPunkId
        );

        group.purchasePrice = offeredPrice;
        group.originalId = museum.mosaicRegistry().create(
            group.targetPunkId,
            group.ticketsBought,
            group.purchasePrice,
            calculateMinReservePrice(group.purchasePrice),
            calculateMaxReservePrice(group.purchasePrice)
        );
        group.status = GroupStatus.Claimable;

        emit GroupWon(groupId);
    }

    // @dev Batch claim-refund as many Mosaic Monos as tickets held
    function claim(
        uint192 groupId
    )
        external
        nonReentrant
        onlyValidGroup(groupId)
        onlyWhenActive
        returns (uint256[] memory mosaicIds)
    {
        Group storage group = CryptoPunksGroupStorage.get().groups[groupId];
        require(
            group.status == GroupStatus.Claimable,
            "The group is not finalized"
        );
        uint256 ticketsHeld = getTickets(msg.sender, groupId);
        require(ticketsHeld > 0, "Only ticket holders can claim tokens");
        _burn(msg.sender, groupId, ticketsHeld);

        // Refund
        uint256 owed = getRefundPerTicket(group) * ticketsHeld;
        // TODO: Define a library for ETH sending with gas considerations
        (bool sent, ) = msg.sender.call{value: owed}("");
        require(sent, "Failed to refund");

        // Mint
        uint256[] memory mosaicIds = new uint256[](ticketsHeld);
        for (uint256 i = 0; i < ticketsHeld; i++) {
            uint256 mosaicId = museum.mosaicRegistry().mint(
                msg.sender,
                group.originalId
            );
            mosaicIds[i] = mosaicId;
            emit Claimed(msg.sender, groupId, mosaicId);
        }

        return mosaicIds;
    }

    // @dev only for lost/expired groups to invoke explicitly
    function refundExpired(
        uint192 groupId
    ) external nonReentrant onlyValidGroup(groupId) onlyWhenActive {
        address payable contributor = payable(msg.sender);
        Group storage group = CryptoPunksGroupStorage.get().groups[groupId];
        require(
            getGroupLifeCycle(groupId) == GroupLifeCycle.Lost,
            "The group must be expired"
        );
        uint256 ticketsHeld = getTickets(msg.sender, groupId);
        require(ticketsHeld > 0, "Only ticket holders can get refunds");

        _burn(msg.sender, groupId, ticketsHeld);

        uint256 owed = getRefundPerTicket(group) * ticketsHeld;
        (bool sent, ) = contributor.call{value: owed}("");
        require(sent, "Failed to refund");
    }

    function getRefundPerTicket(
        Group storage group
    ) private view returns (uint256 refundPerTicket) {
        uint256 surplus = group.totalContribution - group.purchasePrice;

        if (surplus <= 0) {
            return 0;
        }
        return surplus / group.ticketsBought;
    }

    //
    // Registry-related views
    //

    function uri(uint256 groupId) public view override returns (string memory) {
        Group storage group = CryptoPunksGroupStorage.get().groups[
            uint192(groupId)
        ];
        return group.metadataUri;
    }

    //
    // Group-related views
    //

    function getLatestGroupId() public view returns (uint192 latestGroupId) {
        return CryptoPunksGroupStorage.get().latestGroupId;
    }

    function getGroup(
        uint192 groupId
    ) public view onlyValidGroup(groupId) returns (Group memory) {
        return CryptoPunksGroupStorage.get().groups[groupId];
    }

    function getGroupLifeCycle(
        uint192 groupId
    ) public view onlyValidGroup(groupId) returns (GroupLifeCycle) {
        Group storage group = CryptoPunksGroupStorage.get().groups[groupId];
        if (group.status == GroupStatus.Claimable) {
            return GroupLifeCycle.Won;
        }
        if (group.status == GroupStatus.Open) {
            if (group.expiresAt >= block.timestamp) {
                return GroupLifeCycle.Active;
            }
            return GroupLifeCycle.Lost;
        }
        return GroupLifeCycle.Nonexistent;
    }

    function getGroupTotalContribution(
        uint192 groupId
    ) public view onlyValidGroup(groupId) returns (uint256 totalContribution) {
        return CryptoPunksGroupStorage.get().groups[groupId].totalContribution;
    }

    //
    // Ticket
    //

    function getTickets(
        address inquired,
        uint192 groupId
    ) public view returns (uint256) {
        return balanceOf(inquired, uint256(groupId));
    }

    function isCreator(
        address inquired,
        uint192 groupId
    ) public view returns (bool) {
        return
            CryptoPunksGroupStorage.get().groups[groupId].creator == inquired;
    }

    function hasContribution(
        address inquired,
        uint192 groupId
    ) public view returns (bool) {
        return getTickets(inquired, groupId) > 0;
    }

    //
    // Constitution
    //

    function calculateMinReservePrice(
        uint256 purchasePrice
    ) public pure returns (uint256 minReservePrice) {
        return
            BasisPoint.calculateBasisPoint(
                purchasePrice,
                MIN_RESERVE_PRICE_BPS
            );
    }

    function calculateMaxReservePrice(
        uint256 purchasePrice
    ) public pure returns (uint256 maxReservePrice) {
        return
            BasisPoint.calculateBasisPoint(
                purchasePrice,
                MAX_RESERVE_PRICE_BPS
            );
    }

    //
    // Admin
    //

    function airdrop(
        address to,
        uint256[] memory ids,
        uint256[] memory amounts
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _mintBatch(to, ids, amounts, "");
    }

    //
    // Internals
    //
    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        override(ERC1155Upgradeable, AccessControlUpgradeable)
        returns (bool)
    {
        return
            ERC1155Upgradeable.supportsInterface(interfaceId) ||
            AccessControlUpgradeable.supportsInterface(interfaceId);
    }

    function _authorizeUpgrade(
        address
    ) internal override onlyRole(DEFAULT_ADMIN_ROLE) {}

    // TODO: fallback
}

src/CryptoPunksGroupStorage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "./ICryptoPunksGroupRegistry.sol";

library CryptoPunksGroupStorage {
    bytes32 constant POSITION = keccak256("CryptoPunksGroupStorage");

    struct Storage {
        /**
         * @dev Starts from 1.
         *  Must increment this first when creating a new group.
         */
        uint192 latestGroupId;
        mapping(uint192 => UsingCryptoPunksGroupRegistryStructs.Group) groups;
        /**
         * @dev groupId -> address -> shares (= the number of tickets bought)
         */
        mapping(uint192 => mapping(address => uint256)) refundableTickets;
    }

    function get() internal pure returns (Storage storage data) {
        bytes32 position = POSITION;
        assembly {
            data.slot := position
        }
    }
}

src/CryptoPunksMosaicRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {ERC721} from "lib/openzeppelin-contracts/contracts/token/ERC721/ERC721.sol";
import {SafeCast} from "lib/openzeppelin-contracts/contracts/utils/math/SafeCast.sol";
import {AccessControl} from "lib/openzeppelin-contracts/contracts/access/AccessControl.sol";
import {Strings} from "lib/openzeppelin-contracts/contracts/utils/Strings.sol";

import "./lib/BasisPoint.sol";
import "./external/ICryptoPunksMarket.sol";
import "./ICryptoPunksMosaicRegistry.sol";
import "./CryptoPunksGroupRegistry.sol";
import "./CryptoPunksMuseum.sol";

// TODO: Reconsider the ID scheme so that the same origin contract's same groups map to the same ID (contract, group) => (internal id)
contract CryptoPunksMosaicRegistry is
    ICryptoPunksMosaicRegistry,
    ERC721,
    AccessControl
{
    using SafeCast for uint256;

    uint256 private constant MONO_ID_BITS = 64;
    uint256 private constant MONO_ID_BITMASK = (1 << (MONO_ID_BITS + 1)) - 1; // lower 64 bits

    address private constant NO_BIDDER = address(0x0);
    uint40 public constant BID_EXPIRY = 604800;
    uint256 public constant BID_ACCEPTANCE_THRESHOLD_BPS = 5100;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    CryptoPunksMuseum public immutable museum;

    string public invalidMetadataUri;

    //
    // Models
    //

    // @dev used as a `originalId`, starting from 1.
    // TODO: Consider changing to nextOriginalId to avoid more gas consumption for the first group
    uint192 public latestOriginalId;

    mapping(uint192 => Original) public originals;

    // @dev 0 represents the Original; each Mono is assigned an ID starting from 1.
    //  The value represents the next ID to assign for a new Mono.
    //  originalId => nextMonoId
    mapping(uint192 => uint64) public nextMonoIds;

    // @dev mosaicId (originalId + monoId) => Mono
    mapping(uint256 => Mono) public monos;

    //
    // Reconstitution
    //

    // @dev bidId => Bid
    mapping(uint256 => Bid) public bids;
    mapping(uint256 => uint256) public bidDeposits;

    // @dev originalId => value
    mapping(uint192 => uint256) public resalePrices;

    constructor(
        address museumAddress
    ) ERC721("CryptoPunks Mosaic", "PUNKSMOSAIC") {
        museum = CryptoPunksMuseum(museumAddress);
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(DEFAULT_ADMIN_ROLE, museumAddress);
    }

    modifier onlyWhenActive() {
        require(museum.isActive(), "Museum must be active");
        _;
    }

    modifier onlyActiveOriginal(uint192 originalId) {
        require(
            originals[originalId].status == OriginalStatus.Active,
            "Not active"
        );
        _;
    }

    modifier onlyMosaicOwner(uint256 mosaicId) {
        require(ownerOf(mosaicId) == msg.sender, "Must own the Mosaic");
        _;
    }

    //
    // Core
    //

    function create(
        uint256 punkId,
        uint64 totalClaimableCount,
        uint256 purchasePrice,
        uint256 minReservePrice,
        uint256 maxReservePrice
    )
        external
        override
        onlyWhenActive
        onlyRole(MINTER_ROLE)
        returns (uint192 originalId)
    {
        require(
            museum.cryptoPunksMarket().punkIndexToAddress(punkId) ==
                address(this),
            "The contract must own the punk"
        );
        originalId = ++latestOriginalId;
        ++nextMonoIds[originalId];
        originals[originalId] = Original({
            id: originalId,
            punkId: punkId,
            totalMonoSupply: totalClaimableCount,
            claimedMonoCount: 0,
            purchasePrice: purchasePrice,
            minReservePrice: minReservePrice,
            maxReservePrice: maxReservePrice,
            status: OriginalStatus.Active,
            activeBidId: 0,
            metadataBaseUri: ""
        });
        return originalId;
    }

    function mint(
        address contributor,
        uint192 originalId
    )
        external
        override
        onlyWhenActive
        onlyRole(MINTER_ROLE)
        returns (uint256 mosaicId)
    {
        require(
            nextMonoIds[originalId] > 0,
            "Original must be initialized first"
        );
        uint64 monoId = nextMonoIds[originalId]++;
        mosaicId = toMosaicId(originalId, monoId);
        monos[mosaicId] = Mono({
            mosaicId: mosaicId,
            presetId: 0,
            governanceOptions: MonoGovernanceOptions({
                proposedReservePrice: 0,
                bidResponse: MonoBidResponse.None,
                bidId: 0
            })
        });
        originals[originalId].claimedMonoCount++;
        _safeMint(contributor, mosaicId);

        return mosaicId;
    }

    //
    // Design: Mosaic owners
    //

    function setPresetId(
        uint256 mosaicId,
        uint8 presetId
    ) public onlyMosaicOwner(mosaicId) {
        Mono storage mono = monos[mosaicId];
        mono.presetId = presetId;
    }

    //
    // Governance: Mosaic owners
    //

    function proposeReservePrice(
        uint256 mosaicId,
        uint256 price
    ) public onlyWhenActive onlyMosaicOwner(mosaicId) {
        // TODO: Check the bid state requirement
        //  and decide whether to allow reserve price proposals
        //  when there is an ongoing Bid already
        (uint192 originalId, ) = fromMosaicId(mosaicId);
        Original storage original = originals[originalId];
        require(
            original.minReservePrice <= price &&
                price <= original.maxReservePrice,
            "Must be within the range"
        );
        Mono storage mono = monos[mosaicId];
        mono.governanceOptions.proposedReservePrice = price;
    }

    //
    // Reconstitution: bidder
    //

    function bid(
        uint192 originalId,
        uint256 price
    )
        external
        payable
        onlyWhenActive
        onlyActiveOriginal(originalId)
        returns (uint256 newBidId)
    {
        Original storage original = originals[originalId];
        // TODO: Make bid respect min reserve prices decided by GovernanceOptions
        // TODO: Check whether the minimum requirement for initiating bids is met (turnout)
        require(
            price >= original.minReservePrice &&
                price >= getAverageReservePriceProposals(originalId) &&
                price <= original.maxReservePrice,
            "Bid price must be within the reserve price range"
        );
        require(msg.value == price, "Must send the exact value as proposed");

        uint256 oldBidId = original.activeBidId;
        if (oldBidId != 0) {
            // A preceding bid exists, so its state must be updated first
            BidState oldBidState = finalizeProposedBid(oldBidId);
            require(
                oldBidState == BidState.Rejected,
                "The previous bid must be rejected"
            );
        }
        uint256 newBidId = toBidId(originalId, msg.sender, block.timestamp);
        bids[newBidId] = Bid({
            id: newBidId,
            originalId: originalId,
            bidder: payable(msg.sender),
            createdAt: uint40(block.timestamp),
            expiry: BID_EXPIRY,
            price: price,
            state: BidState.Proposed
        });
        bidDeposits[newBidId] = msg.value;
        original.activeBidId = newBidId;

        emit BidProposed(newBidId, originalId);
        return newBidId;
    }

    function toBidId(
        uint192 originalId,
        address bidder,
        uint256 blockTimestamp
    ) public pure returns (uint256 id) {
        return
            uint256(
                keccak256(
                    abi.encodePacked(originalId, bidder, uint40(blockTimestamp))
                )
            );
    }

    function refundBidDeposit(uint256 bidId) external onlyWhenActive {
        Bid storage bid = bids[bidId];
        require(
            bid.state == BidState.Rejected,
            "Only rejected bids can be refunded"
        );
        require(
            bid.bidder == msg.sender,
            "Only the bidder can retrieve its own fund"
        );

        uint256 deposit = bidDeposits[bidId];
        (bool sent, ) = msg.sender.call{value: deposit}("");
        require(sent, "Failed to refund");

        bid.state = BidState.Refunded;
        emit BidRefunded(bidId, bid.originalId);
    }

    //
    // Reconstitution: Mosaic owners
    //

    function respondToBid(
        uint256 mosaicId,
        MonoBidResponse response
    ) public onlyWhenActive onlyMosaicOwner(mosaicId) {
        (uint192 originalId, ) = fromMosaicId(mosaicId);
        require(hasOngoingBid(originalId), "No bid ongoing");
        MonoGovernanceOptions storage governanceOptions = monos[mosaicId]
            .governanceOptions;
        governanceOptions.bidId = originals[originalId].activeBidId;
        governanceOptions.bidResponse = response;
    }

    //
    // Reconstitution: common
    //

    function finalizeProposedBid(
        uint256 bidId
    ) public onlyWhenActive returns (BidState) {
        // TODO: Double-check the prerequisites, including Original check
        Bid storage bid = bids[bidId];
        require(
            bid.state == BidState.Proposed,
            "Only bids in proposal can be updated"
        );
        require(
            bid.createdAt + bid.expiry < block.timestamp,
            "Bid vote is ongoing"
        );
        bid.state = isBidAcceptable(bid.originalId)
            ? BidState.Accepted
            : BidState.Rejected;

        if (bid.state == BidState.Accepted) {
            emit BidAccepted(bidId, bid.originalId);
        } else {
            emit BidRejected(bidId, bid.originalId);
        }
        return bid.state;
    }

    // TODO: Introduce a way for Mosaic owners to force Bid finalization to prevent limbo cases where
    //  the winning bidder makes no further transaction
    function finalizeAcceptedBid(uint256 bidId) public onlyWhenActive {
        Bid storage bid = bids[bidId];
        require(bid.state == BidState.Accepted, "Bid must be accepted");

        Original storage original = originals[bid.originalId];
        resalePrices[original.id] = bid.price;
        original.status = OriginalStatus.Sold;
        emit OriginalSold(bid.originalId, bidId);

        museum.cryptoPunksMarket().transferPunk(bid.bidder, original.punkId);

        bid.state = BidState.Won;
        emit BidWon(bidId, bid.originalId);
    }

    //
    // Post-reconstitution: Mosaic owners
    //

    // @dev Burn all owned Monos and send refunds
    function refundOnSold(
        uint192 originalId
    ) public onlyWhenActive returns (uint256 totalResaleFund) {
        // TODO: Double-check whether arithmetic division may cause under/over-refunding
        uint256 burnedMonoCount = 0;
        uint64 nextMonoId = nextMonoIds[originalId];
        for (uint64 monoId = 1; monoId < nextMonoId; monoId++) {
            uint256 mosaicId = toMosaicId(originalId, monoId);
            if (_ownerOf(mosaicId) == msg.sender) {
                _burn(mosaicId);
                burnedMonoCount++;
            }
        }
        require(burnedMonoCount > 0, "No Monos owned to refund");

        totalResaleFund = burnedMonoCount * getPerMonoResaleFund(originalId);
        (bool sent, ) = msg.sender.call{value: totalResaleFund}("");
        require(sent, "Failed to refund");

        emit MonoRefunded(originalId, msg.sender);
    }

    //
    // Reconstitution helpers
    //

    function getAverageReservePriceProposals(
        uint192 originalId
    ) public view returns (uint256 average) {
        // TODO: Consider governance turnout requirements and write a unit test
        (uint64 count, uint256 sum) = sumReservePriceProposals(originalId);
        return sum / count;
    }

    function sumReservePriceProposals(
        uint192 originalId
    ) public view returns (uint64 validProposalCount, uint256 priceSum) {
        uint64 nextMonoId = nextMonoIds[originalId];
        for (uint64 monoId = 1; monoId < nextMonoId; monoId++) {
            Mono storage mono = monos[toMosaicId(originalId, monoId)];
            if (mono.governanceOptions.proposedReservePrice > 0) {
                validProposalCount++;
                priceSum += mono.governanceOptions.proposedReservePrice;
            }
        }
        return (validProposalCount, priceSum);
    }

    function sumBidResponses(
        uint192 originalId
    ) public view virtual returns (uint64 yes, uint64 no) {
        if (!hasOngoingBid(originalId)) {
            return (0, 0);
        }
        uint64 nextMonoId = nextMonoIds[originalId];
        uint256 activeBidId = originals[originalId].activeBidId;
        for (uint64 monoId = 1; monoId < nextMonoId; monoId++) {
            MonoGovernanceOptions storage options = monos[
                toMosaicId(originalId, monoId)
            ].governanceOptions;
            if (options.bidId == activeBidId) {
                if (options.bidResponse == MonoBidResponse.Yes) {
                    yes++;
                } else if (options.bidResponse == MonoBidResponse.No) {
                    no++;
                }
            }
        }
        return (yes, no);
    }

    function isBidAcceptable(
        uint192 originalId
    ) public view virtual returns (bool) {
        // TODO(@jyterencekim): Revisit the bid acceptance condition with respect to the planned spec
        (uint64 yes, ) = sumBidResponses(originalId);
        uint128 totalVotable = originals[originalId].totalMonoSupply;
        return
            yes >=
            BasisPoint.calculateBasisPoint(
                totalVotable,
                BID_ACCEPTANCE_THRESHOLD_BPS
            );
    }

    function getPerMonoResaleFund(
        uint192 originalId
    ) public view virtual returns (uint256 perMonoResaleFund) {
        uint256 resalePrice = resalePrices[originalId];
        require(resalePrice > 0, "No resale price set");
        uint256 perMonoBps = BasisPoint.WHOLE_BPS /
            originals[originalId].totalMonoSupply;

        return BasisPoint.calculateBasisPoint(resalePrice, perMonoBps);
    }

    //
    // Model views
    //
    function getMono(
        uint192 originalId,
        uint64 monoId
    ) external view returns (Mono memory) {
        return monos[toMosaicId(originalId, monoId)];
    }

    function getOriginal(
        uint192 originalId
    ) external view returns (Original memory) {
        return originals[originalId];
    }

    function getOriginalFromMosaicId(
        uint256 mosaicId
    ) external view returns (Original memory) {
        (uint192 originalId, ) = fromMosaicId(mosaicId);
        return originals[originalId];
    }

    function getMonoLifeCycle(
        uint256 mosaicId
    ) public view returns (MonoLifeCycle) {
        (uint192 originalId, ) = fromMosaicId(mosaicId);
        require(originalId <= latestOriginalId, "Invalid originalId");

        Original storage original = originals[originalId];
        if (original.status == OriginalStatus.Sold) {
            return MonoLifeCycle.Dead;
        }
        if (monos[mosaicId].presetId == 0) {
            return MonoLifeCycle.Raw;
        }
        return MonoLifeCycle.Active;
    }

    // TODO(@jyterencekim): Revisit the conditions
    function hasOngoingBid(uint192 originalId) public view returns (bool) {
        uint256 bidId = originals[originalId].activeBidId;
        Bid storage bid = bids[bidId];
        return
            bidId != 0 &&
            bid.bidder != NO_BIDDER &&
            bid.createdAt + bid.expiry >= block.timestamp;
    }

    //
    // Internal Helpers
    //

    function toMosaicId(
        uint192 originalId,
        uint64 monoId
    ) public pure returns (uint256 mosaicId) {
        return (uint256(originalId) << MONO_ID_BITS) | uint256(monoId);
    }

    function fromMosaicId(
        uint256 mosaicId
    ) public pure returns (uint192 originalId, uint64 monoId) {
        return (
            uint192(mosaicId >> MONO_ID_BITS),
            uint64(mosaicId & MONO_ID_BITMASK)
        );
    }

    //
    // Implementation internals
    //

    function grantMintAuthority(
        address addr
    ) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        return _grantRole(MINTER_ROLE, addr);
    }

    function setInvalidMetadataUri(
        string calldata _uri
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        invalidMetadataUri = _uri;
    }

    function setMetadataBaseUri(
        uint192 originalId,
        string calldata _uri
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        Original storage original = originals[originalId];
        original.metadataBaseUri = _uri;
    }

    //
    // ERC721
    //
    function tokenURI(
        uint256 mosaicId
    ) public view override returns (string memory) {
        (uint192 originalId, ) = fromMosaicId(mosaicId);
        Original storage original = originals[originalId];
        if (original.status == OriginalStatus.Sold) {
            return invalidMetadataUri;
        }
        string memory baseUrl = original.metadataBaseUri;
        uint8 presetId = monos[mosaicId].presetId;
        return
            string.concat(
                baseUrl,
                "/",
                Strings.toString(original.punkId),
                "_",
                Strings.toString(uint256(presetId)),
                ".json"
            );
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view override(ERC721, AccessControl) returns (bool) {
        return
            ERC721.supportsInterface(interfaceId) ||
            AccessControl.supportsInterface(interfaceId);
    }
}

