contract ERC1967Proxy is Proxy, ERC1967Upgrade {
    /**
     * @dev Initializes the upgradeable proxy with an initial implementation specified by `_logic`.
     *
     * If `_data` is nonempty, it's used as data in a delegate call to `_logic`. This will typically be an encoded
     * function call, and allows initializing the storage of the proxy like a Solidity constructor.
     */
    constructor(address _logic, bytes memory _data) payable {
        _upgradeToAndCall(_logic, _data, false);
    }

    /**
     * @dev Returns the current implementation address.
     */
    function _implementation() internal view virtual override returns (address impl) {
        return ERC1967Upgrade._getImplementation();
    }
}

src/Lock.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

// Uncomment this line to use console.log
// import "hardhat/console.sol";

contract Lock {
    uint public unlockTime;
    address payable public owner;

    event Withdrawal(uint amount, uint when);

    constructor(uint _unlockTime) payable {
        require(
            block.timestamp < _unlockTime,
            "Unlock time should be in the future"
        );

        unlockTime = _unlockTime;
        owner = payable(msg.sender);
    }

    function withdraw() public {
        // Uncomment this line, and the import of "hardhat/console.sol", to print a log in your terminal
        // console.log("Unlock time is %o and block timestamp is %o", unlockTime, block.timestamp);

        require(block.timestamp >= unlockTime, "You can't withdraw yet");
        require(msg.sender == owner, "You aren't the owner");

        emit Withdrawal(address(this).balance, block.timestamp);

        owner.transfer(address(this).balance);
    }
}

src/UsingCryptoPunksGroupRegistryStructs.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

interface UsingCryptoPunksGroupRegistryStructs {
    enum GroupStatus {
        Invalid,
        Open,
        Claimable
    }

    enum GroupLifeCycle {
        // the group does not exist
        Nonexistent,
        // the group is open and funding is ongoing
        Active,
        // the group has not procured the target original within the expiry
        Lost,
        // the group has successfully purchased and acquired the target original
        Won
    }

    struct Group {
        uint192 id;
        address creator;
        uint256 targetPunkId;
        uint256 targetMaxPrice;
        // @dev Must be a divisor (10, 100, etc.)
        uint64 totalTicketSupply;
        uint256 unitTicketPrice;
        uint256 totalContribution;
        uint64 ticketsBought;
        uint40 expiresAt; // inclusive, in seconds, as in block.timestamp
        GroupStatus status;
        uint256 purchasePrice; // price at which the target is bought
        uint192 originalId;
        string metadataUri;
    }

    event GroupCreated(
        uint192 indexed groupId,
        address indexed creator,
        uint256 targetMaxPrice,
        uint64 totalTicketSupply,
        uint256 unitTicketPrice
    );

    event GroupWon(uint192 indexed groupId);

    event Contributed(
        address indexed contributor,
        uint192 indexed groupId,
        uint96 indexed ticketQuantity
    );

    event Claimed(
        address indexed claimer,
        uint192 indexed groupId,
        uint256 indexed mosaicId
    );
}

src/UsingCryptoPunksMosaicRegistryStruct.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

interface UsingCryptoPunksMosaicRegistryStructs {
    struct Original {
        uint192 id;
        // @dev punkIndex
        uint256 punkId;
        /**
         * @dev To calculate governance quorum and token circulation.
         *      Corresponds to total ticket circulation per group.
         */
        uint96 totalMonoSupply;
        uint96 claimedMonoCount;
        uint256 purchasePrice;
        uint256 minReservePrice;
        uint256 maxReservePrice;
        string metadataBaseUri;
        OriginalStatus status;
        uint256 activeBidId;
    }

    enum OriginalStatus {
        Active,
        Sold
    }

    event OriginalSold(uint192 indexed originalId, uint256 indexed bidId);

    struct Mono {
        uint256 mosaicId;
        uint8 presetId;
        MonoGovernanceOptions governanceOptions;
    }

    enum MonoLifeCycle {
        // @dev pre-design, just minted
        Raw,
        // @dev post-design, valid
        Active,
        // @dev belonging to invalid/reconstituted Original
        Dead
    }

    struct MonoGovernanceOptions {
        uint256 proposedReservePrice;
        MonoBidResponse bidResponse;
        // @dev Bid ID
        uint256 bidId;
    }

    enum MonoBidResponse {
        None,
        Yes,
        No
    }

    event MonoRefunded(uint192 indexed originalId, address indexed monoOwner);

    // @dev There can be at most one ongoing bid per original
    struct Bid {
        // @dev keccak256(abi.encodePacked(originalId, bidder, uint40(block.timestamp))
        uint256 id;
        address payable bidder;
        uint192 originalId;
        uint256 price;
        // block.timestamp
        uint40 createdAt;
        // duration in block.timestamp, in seconds
        uint40 expiry;
        // @dev updated upon any following Bid's creation or explicit external state updates
        BidState state;
    }

    enum BidState {
        // Initial state, awaiting the result until the bidder explicitly reconstitutes the original or admits failure
        Proposed,
        // Resulting states upon vote results
        Accepted,
        Rejected,
        // Final/terminal states after bidder's action
        Won,
        Refunded
    }

    event BidProposed(uint256 indexed bidId, uint192 indexed originalId);

    event BidAccepted(uint256 indexed bidId, uint192 indexed originalId);

    event BidRejected(uint256 indexed bidId, uint192 indexed originalId);

    event BidWon(uint256 indexed bidId, uint192 indexed originalId);

    event BidRefunded(uint256 indexed bidId, uint192 indexed originalId);
}

src/util/DeployByteCode.sol.content:
pragma solidity ^0.8.17;

contract DeployByteCode {
    function deploy(bytes memory _bytecode) external returns (address addr) {
        assembly {
            addr := create(0, add(_bytecode, 0x20), mload(_bytecode))
            if iszero(extcodesize(addr)) {
                revert(0, 0)
            }
        }
    }
}
