pragma solidity ^0.8.17;

interface UsingCryptoPunksGroupRegistryStructs {
    /**
     * @dev WON if the group has successfully purchased and acquired the target original;
     *  LOST if the group has not procured the target original within the expiry.
     */
    enum GroupStatus {
        None,
        Open,
        Won,
        Lost,
        Claimable
    }

    struct Group {
        uint192 id;
        address creator;
        uint256 targetPunkId;
        uint256 targetMaxPrice;
        uint64 totalTicketSupply;
        uint256 unitTicketPrice;
        uint256 totalContribution;
        uint64 ticketsBought;
        uint40 expiry; // in seconds, with respect to block.timestamp
        GroupStatus status;
        uint256 purchasePrice; // price at which the target is bought
        uint192 originalId;
        string metadataUri;
    }

    event GroupCreated(
        uint192 indexed groupId,
        address indexed creator,
        uint256 targetMaxPrice,
        uint64 totalTicketSupply,
        uint256 unitTicketPrice
    );

    event GroupWon(uint192 indexed groupId);

    event Contributed(
        address indexed contributor,
        uint192 indexed groupId,
        uint96 indexed ticketQuantity
    );

    event Claimed(
        address indexed claimer,
        uint192 indexed groupId,
        uint256 indexed mosaicId
    );
}
","keccak256":"0x26c885d7b00fcb6ca50e2e24f7f1e6c8fe92a1fff9d36a3d825889f9c6e719de","license":"MIT"},"src/UsingCryptoPunksMosaicRegistryStructs.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

interface UsingCryptoPunksMosaicRegistryStructs {
    struct Original {
        uint192 id;
        // @dev punkIndex
        uint256 punkId;
        /**
         * @dev To calculate governance quorum and token circulation.
         *      Corresponds to total ticket circulation per group.
         */
        uint128 totalMonoSupply;
        uint128 claimedMonoCount;
        uint256 purchasePrice;
        uint256 minReservePrice;
        uint256 maxReservePrice;
        string metadataBaseUri;
        OriginalStatus status;
        uint256 activeBidId;
    }

    enum OriginalStatus {
        Active,
        Sold
    }

    struct Mono {
        uint256 mosaicId;
        uint8 presetId;
        MonoGovernanceOptions governanceOptions;
    }

    enum MonoLifeCycle {
        // @dev pre-design, just minted
        Raw,
        // @dev post-design, valid
        Active,
        // @dev belonging to invalid/reconstituted Original
        Dead
    }

    struct MonoGovernanceOptions {
        uint256 proposedReservePrice;
        MonoBidResponse bidResponse;
        // @dev Bid ID
        uint256 bidId;
    }

    enum MonoBidResponse {
        None,
        Yes,
        No
    }

    // @dev There can be at most one ongoing bid per original
    struct Bid {
        // @dev keccak256(abi.encodePacked(originalId, bidder, uint40(block.timestamp))
        uint256 id;
        address payable bidder;
        uint192 originalId;
        uint256 price;
        // block.timestamp
        uint40 createdAt;
        // duration in block.timestamp, in seconds
        uint40 expiry;
        // @dev updated upon any following Bid's creation or explicit external state updates
        BidState state;
    }

    enum BidState {
        // Initial state, awaiting the result until the bidder explicitly reconstitutes the original or admits failure
        Proposed,
        // Resulting states upon vote results
        Accepted,
        Rejected,
        // Final/terminal states after bidder's action
        Won,
        Refunded
    }
}
